#!/bin/bash
echo "1"

takedown() {
    rm -rf trouble.txt
}

regex='([0-9]{20}),([0-9]{20}),([0-9]{20}),([0-9]{20}),([0-9]{20})'

get_db_entry() {
    dbtext=$(cat ../database.csv)
    uid_topmost="$1${dbtext##*$'$1'}" 
    # the entry we want is at the beginning of the text so regex can match it

    if [[ $uid_topmost =~ $regex ]]; then
        # trim off leading zeroes and populate variables
        user_id=$((10#${BASH_REMATCH[1]}))
        byte_total=$((10#${BASH_REMATCH[2]}))
        byte_quota=$((10#${BASH_REMATCH[3]}))
        file_total=$((10#${BASH_REMATCH[4]}))
        file_quota=$((10#${BASH_REMATCH[5]}))
    fi
}

# test
current_user_id=$(printf "%020d" $UID)

# get quota so we know how much to write in order to exceed
get_db_entry $current_user_id
guaranteed_error_writes=$(expr $byte_quota / 5000)
# maximum number of writes to guarantee exceeding the quota when starting from 0 in the for loop

for i in $( seq 0 $guaranteed_error_writes )
do
    printf "This text is 5,000 bytes long... and I don't really know a better way to do this... \n" >> trouble.txt
done

logtext=$(cat ../log.txt)
recent_logtext="write:${logtext##*$'write:'}"
quota_msg='QUOTA has been reached!'
# only match on quota exceeded since the most recent write
if [[ $logtext =~ $quota_msg ]]; then
    echo "0"
    takedown
    exit
fi

echo "1"
takedown
exit